const errorsList = [];

// Functions 
function getDocText(tableRowIndex, tableCellIndex, contentIndex=0, bodyContentIndex = 2, elementsIndex = 0) {
  try {
    // The proper order is body Content -> table Row -> table Cell -> content -> element.
    // Extract the paragraph and textRun elements for validation
    const paragraphElement = $json?.body?.content?.[bodyContentIndex]?.table?.tableRows?.[tableRowIndex]?.tableCells?.[tableCellIndex]?.content?.[contentIndex]?.paragraph?.elements?.[elementsIndex]?.textRun;

    // If the paragraph or textRun is missing, return null
    if (!paragraphElement) {
      return null;
    }

    // Extract and clean the content
    const cellContent = paragraphElement.content.trim().replace(/\n/g, '');

    // If the content is an empty string (""), return N/A
    return cellContent === "" ? "N/A" : cellContent;

  } catch (error) {
    errorsList.push(error);
    console.error(`Error fetching text for tableRowIndex: ${tableRowIndex}, tableCellIndex: ${tableCellIndex}, contentIndex: ${contentIndex}`, error);
    return null;
  }
}

