const errorsList = [];

// Function to extract and concatenate text from elements
function getDocText(tableRowIndex, tableCellIndex, contentIndex = 0, bodyContentIndex = 2, elementsIndex = 0) {
  try {
    // The proper order is body Content -> table Row -> table Cell -> content -> element.
    const paragraphElement = $json?.body?.content?.[bodyContentIndex]?.table?.tableRows?.[tableRowIndex]?.tableCells?.[tableCellIndex]?.content?.[contentIndex]?.paragraph?.elements;

    // If the paragraph or elements are missing, return null
    if (!paragraphElement || !Array.isArray(paragraphElement) || paragraphElement.length === 0) {
      return null;
    }

    // Check the length of elements and concatenate if more than one
    const cellContent = paragraphElement.length > 1 
      ? paragraphElement.map(el => el.textRun?.content.trim().replace(/\n/g, '')).join(" ") // Join multiple elements
      : paragraphElement[0]?.textRun?.content.trim().replace(/\n/g, ''); // Default to the first element

    // If the content is an empty string (""), return N/A
    return cellContent === "" ? "N/A" : cellContent;

  } catch (error) {
    errorsList.push(error);
    console.error(`Error fetching text for tableRowIndex: ${tableRowIndex}, tableCellIndex: ${tableCellIndex}, contentIndex: ${contentIndex}`, error);
    return null;
  }
}

